package org.iets3.core.expr.base.intentions;

/*Generated by MPS */

import org.jetbrains.mps.openapi.module.SRepository;
import javax.swing.JWindow;
import javax.swing.JLabel;
import javax.swing.BorderFactory;
import javax.swing.SwingUtilities;

public class CommandWithMessage {

  public static void execute(final String message, final SRepository repository, final Runnable runnable) {
    final JWindow win = new JWindow();
    JLabel label = new JLabel(message);
    label.setBorder(BorderFactory.createEmptyBorder(20, 50, 20, 50));
    win.add(label);
    win.pack();
    win.setLocationRelativeTo(null);
    win.setVisible(true);

    // The window needs some events until it gets painted. 
    SwingUtilities.invokeLater(new Runnable() {
      public void run() {
        SwingUtilities.invokeLater(new Runnable() {
          public void run() {
            SwingUtilities.invokeLater(new Runnable() {
              public void run() {
                try {
                  repository.getModelAccess().executeCommand(new Runnable() {
                    public void run() {
                      runnable.run();
                    }
                  });
                } finally {
                  win.setVisible(false);
                }
              }
            });
          }
        });
      }
    });
  }

}
